select *, IF(COALESCE(`proName`, '') = 'TCL', 100, 0) + IF(COALESCE(`proName`, '') LIKE 'TCL%', 50, 0) + IF(COALESCE(`proName`, '') LIKE 'T% C% L%', 42, 0) + IF( REPLACE(COALESCE(`proName`, ''), '\.', '') LIKE '%T%C%L%', ROUND(40 * ( CHAR_LENGTH( 'TCL' ) / CHAR_LENGTH( REPLACE(COALESCE(`proName`, ''), ' ', '') ))), 0) + IF(COALESCE(`proName`, '') LIKE 'TCL%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proName`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proName`, '') ), ' ', '')) AND COALESCE(`proName`, '') LIKE BINARY 'T%C%L%', 32, 0) + IF(COALESCE(`proName`, '') LIKE '%TCL%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proName`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proName`, '')), lower('TCL'), ''))
) / LENGTH('TCL')) + IF(COALESCE(`proDescription`, '') = 'TCL', 100, 0) + IF(COALESCE(`proDescription`, '') LIKE 'TCL%', 50, 0) + IF(COALESCE(`proDescription`, '') LIKE 'T% C% L%', 42, 0) + IF( REPLACE(COALESCE(`proDescription`, ''), '\.', '') LIKE '%T%C%L%', ROUND(40 * ( CHAR_LENGTH( 'TCL' ) / CHAR_LENGTH( REPLACE(COALESCE(`proDescription`, ''), ' ', '') ))), 0) + IF(COALESCE(`proDescription`, '') LIKE 'TCL%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proDescription`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proDescription`, '') ), ' ', '')) AND COALESCE(`proDescription`, '') LIKE BINARY 'T%C%L%', 32, 0) + IF(COALESCE(`proDescription`, '') LIKE '%TCL%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proDescription`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proDescription`, '')), lower('TCL'), ''))
) / LENGTH('TCL')) AS relevance from `products` having `relevance` > '0' and `relevance` > '20' order by `relevance` desc

