select *, IF(COALESCE(`proName`, '') = '电视', 100, 0) + IF(COALESCE(`proName`, '') LIKE '电视%', 50, 0) + IF(COALESCE(`proName`, '') LIKE '电% 视%', 42, 0) + IF( REPLACE(COALESCE(`proName`, ''), '\.', '') LIKE '%电%视%', ROUND(40 * ( CHAR_LENGTH( '电视' ) / CHAR_LENGTH( REPLACE(COALESCE(`proName`, ''), ' ', '') ))), 0) + IF(COALESCE(`proName`, '') LIKE '电视%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proName`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proName`, '') ), ' ', '')) AND COALESCE(`proName`, '') LIKE BINARY '电%视%', 32, 0) + IF(COALESCE(`proName`, '') LIKE '%电视%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proName`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proName`, '')), lower('电视'), ''))
) / LENGTH('电视')) + IF(COALESCE(`proDescription`, '') = '电视', 100, 0) + IF(COALESCE(`proDescription`, '') LIKE '电视%', 50, 0) + IF(COALESCE(`proDescription`, '') LIKE '电% 视%', 42, 0) + IF( REPLACE(COALESCE(`proDescription`, ''), '\.', '') LIKE '%电%视%', ROUND(40 * ( CHAR_LENGTH( '电视' ) / CHAR_LENGTH( REPLACE(COALESCE(`proDescription`, ''), ' ', '') ))), 0) + IF(COALESCE(`proDescription`, '') LIKE '电视%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proDescription`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proDescription`, '') ), ' ', '')) AND COALESCE(`proDescription`, '') LIKE BINARY '电%视%', 32, 0) + IF(COALESCE(`proDescription`, '') LIKE '%电视%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proDescription`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proDescription`, '')), lower('电视'), ''))
) / LENGTH('电视')) AS relevance from `products` having `relevance` > '0' and `relevance` > '20' order by `relevance` desc

