select *, IF(COALESCE(`proName`, '') = '松下', 100, 0) + IF(COALESCE(`proName`, '') LIKE '松下%', 50, 0) + IF(COALESCE(`proName`, '') LIKE '松% 下%', 42, 0) + IF( REPLACE(COALESCE(`proName`, ''), '\.', '') LIKE '%松%下%', ROUND(40 * ( CHAR_LENGTH( '松下' ) / CHAR_LENGTH( REPLACE(COALESCE(`proName`, ''), ' ', '') ))), 0) + IF(COALESCE(`proName`, '') LIKE '松下%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proName`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proName`, '') ), ' ', '')) AND COALESCE(`proName`, '') LIKE BINARY '松%下%', 32, 0) + IF(COALESCE(`proName`, '') LIKE '%松下%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proName`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proName`, '')), lower('松下'), ''))
) / LENGTH('松下')) + IF(COALESCE(`proDescription`, '') = '松下', 100, 0) + IF(COALESCE(`proDescription`, '') LIKE '松下%', 50, 0) + IF(COALESCE(`proDescription`, '') LIKE '松% 下%', 42, 0) + IF( REPLACE(COALESCE(`proDescription`, ''), '\.', '') LIKE '%松%下%', ROUND(40 * ( CHAR_LENGTH( '松下' ) / CHAR_LENGTH( REPLACE(COALESCE(`proDescription`, ''), ' ', '') ))), 0) + IF(COALESCE(`proDescription`, '') LIKE '松下%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proDescription`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proDescription`, '') ), ' ', '')) AND COALESCE(`proDescription`, '') LIKE BINARY '松%下%', 32, 0) + IF(COALESCE(`proDescription`, '') LIKE '%松下%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proDescription`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proDescription`, '')), lower('松下'), ''))
) / LENGTH('松下')) AS relevance from `products` having `relevance` > '0' and `relevance` > '20' order by `relevance` desc

