select *, IF(COALESCE(`proName`, '') = '家用电器', 100, 0) + IF(COALESCE(`proName`, '') LIKE '家用电器%', 50, 0) + IF(COALESCE(`proName`, '') LIKE '家% 用% 电% 器%', 42, 0) + IF( REPLACE(COALESCE(`proName`, ''), '\.', '') LIKE '%家%用%电%器%', ROUND(40 * ( CHAR_LENGTH( '家用电器' ) / CHAR_LENGTH( REPLACE(COALESCE(`proName`, ''), ' ', '') ))), 0) + IF(COALESCE(`proName`, '') LIKE '家用电器%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proName`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proName`, '') ), ' ', '')) AND COALESCE(`proName`, '') LIKE BINARY '家%用%电%器%', 32, 0) + IF(COALESCE(`proName`, '') LIKE '%家用电器%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proName`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proName`, '')), lower('家用电器'), ''))
) / LENGTH('家用电器')) + IF(COALESCE(`proDescription`, '') = '家用电器', 100, 0) + IF(COALESCE(`proDescription`, '') LIKE '家用电器%', 50, 0) + IF(COALESCE(`proDescription`, '') LIKE '家% 用% 电% 器%', 42, 0) + IF( REPLACE(COALESCE(`proDescription`, ''), '\.', '') LIKE '%家%用%电%器%', ROUND(40 * ( CHAR_LENGTH( '家用电器' ) / CHAR_LENGTH( REPLACE(COALESCE(`proDescription`, ''), ' ', '') ))), 0) + IF(COALESCE(`proDescription`, '') LIKE '家用电器%', 35, 0) + IF( CHAR_LENGTH( TRIM( COALESCE(`proDescription`, '') )) = CHAR_LENGTH( REPLACE( TRIM( COALESCE(`proDescription`, '') ), ' ', '')) AND COALESCE(`proDescription`, '') LIKE BINARY '家%用%电%器%', 32, 0) + IF(COALESCE(`proDescription`, '') LIKE '%家用电器%', 30, 0) + 8 * ROUND((
CHAR_LENGTH(COALESCE(`proDescription`, '')) - CHAR_LENGTH( REPLACE( LOWER(COALESCE(`proDescription`, '')), lower('家用电器'), ''))
) / LENGTH('家用电器')) AS relevance from `products` having `relevance` > '0' and `relevance` > '20' order by `relevance` desc

